let pushIndex = 0

const getNextPush = () => {
    return input.charAt(pushIndex++ % input.length)
}

class Block {
    constructor(coords, room) {
        this.coords = coords
        this.room = room
    }

    drop() {
        let attempt = []
        this.coords.forEach(({x, y}) => attempt.push({x, y}))

        if (getNextPush() === '<') {
            attempt.forEach((coord) => coord.x--)
        } else {
            attempt.forEach((coord) => coord.x++)
        }

        const illegal = attempt.some(({x, y}) => {
            if (x === -1) return true
            if (x === 7) return true
            return this.room[x][y]
        })
        if(!illegal) {
            this.coords = attempt
        }
        attempt = []
        this.coords.forEach(({x, y}) => attempt.push({x, y}))

        attempt.forEach((coord) => coord.y--)

        const dropped =  attempt.some(({x, y}) => y < 0 || this.room[x][y])

        if (dropped) return true

        this.coords = attempt
        return false
    }

    integrateWithRoom() {
        this.coords.forEach(({x, y}) => {
            highest = Math.max(highest, y)
            this.room[x][y] = true
        })
    }
}

class LangePlat extends Block{
    constructor(room) {
        super([{x:2, y:highest+0+4}, {x:3, y:highest+0+4}, {x:4, y:highest+0+4}, {x:5, y:highest+0+4}], room)
    }
}

class LangeRecht extends Block{
    constructor(room) {
        super([{x:2, y:highest+0+4}, {x:2, y:highest+1+4}, {x:2, y:highest+2+4}, {x:2, y:highest+3+4}], room)
    }
}

class Blok extends Block{
    constructor(room) {
        super([{x:2, y:highest+0+4}, {x:2, y:highest+1+4}, {x:3, y:highest+0+4}, {x:3, y:highest+1+4}], room)
    }
}

class Kruis extends Block{
    constructor(room) {
        super([{x:2, y:highest+1+4}, {x:3, y:highest+0+4}, {x:3, y:highest+1+4}, {x:3, y:highest+2+4}, {x:4, y:highest+1+4}], room)
    }
}

class LVorm extends Block{
    constructor(room) {
        super([{x:2, y:highest+0+4}, {x:3, y:highest+0+4}, {x:4, y:highest+0+4}, {x:4, y:highest+1+4}, {x:4, y:highest+2+4}], room)
    }
}

let blockCount = 0
let highest = -1
let room = [[],[],[],[],[],[],[]]

const getNextBlock = () => {
    switch (blockCount++ % 5) {
        case 0: return new LangePlat(room)
        case 1: return new Kruis(room)
        case 2: return new LVorm(room)
        case 3: return new LangeRecht(room)
        case 4: return new Blok(room)
    }
}



for (let i = 0; i < 2022; i++) {
    const block = getNextBlock()
    while(!block.drop()) {}
    block.integrateWithRoom()
}
